{"metadata":{"kernelspec":{"language":"python","display_name":"Python 3","name":"python3"},"language_info":{"pygments_lexer":"ipython3","nbconvert_exporter":"python","version":"3.6.4","file_extension":".py","codemirror_mode":{"name":"ipython","version":3},"name":"python","mimetype":"text/x-python"},"kaggle":{"accelerator":"none","dataSources":[],"isInternetEnabled":true,"language":"python","sourceType":"script","isGpuEnabled":false}},"nbformat_minor":4,"nbformat":4,"cells":[{"cell_type":"code","source":"import logging\nfrom IPython.display import display\n\nclass DisplayHandler(logging.Handler):\n    def emit(self, record):\n        display(self.format(record))\n\n# Configure logging\nlogger = logging.getLogger(\"project_logger\")\nlogger.setLevel(logging.INFO)\n\n# Avoid adding handlers multiple times\nif not logger.hasHandlers():\n    # Add custom display handler\n    display_handler = DisplayHandler()\n    display_handler.setFormatter(logging.Formatter('%(asctime)s - %(levelname)s - %(message)s'))\n    logger.addHandler(display_handler)\n","metadata":{"_uuid":"8f2839f25d086af736a60e9eeb907d3b93b6e0e5","_cell_guid":"b1076dfc-b9ad-4769-8c92-a6c4dae69d19","trusted":true},"execution_count":null,"outputs":[]}]}